#!/bin/bash
# ============================================
# Multicall3 Keyless Deployment Script
# ============================================
# Purpose: Deploy Multicall3 contract to the canonical address
#          0xcA11bde05977b3631167028862bE2a173976CA11
# Method: Keyless deployment using pre-signed raw transaction
# Reference: https://github.com/mds1/multicall

set -e  # Exit on error

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Configuration
RPC_URL="${RPC_URL:-http://localhost:8545}"
DEPLOYER_ADDRESS="0x05f32B3cC3888453ff71B01135B34FF8e41263F2"
MULTICALL3_ADDRESS="0xcA11bde05977b3631167028862bE2a173976CA11"
REQUIRED_BALANCE="100000000000000"  # 0.0001 ETH in wei (enough for deployment)

# Pre-signed transaction for Multicall3 deployment
# This transaction was signed by the deployer (0x05f32B3cC3888453ff71B01135B34FF8e41263F2)
# and will always deploy to the same address on any EVM chain
RAW_TX=""

echo -e "${BLUE}╔════════════════════════════════════════════════════╗${NC}"
echo -e "${BLUE}║     Multicall3 Keyless Deployment Script          ║${NC}"
echo -e "${BLUE}╚════════════════════════════════════════════════════╝${NC}"
echo ""

# Check if cast is installed
if ! command -v cast &> /dev/null; then
    echo -e "${RED}❌ Error: 'cast' command not found${NC}"
    echo -e "${YELLOW}Please install Foundry: https://book.getfoundry.sh/getting-started/installation${NC}"
    exit 1
fi

echo -e "${BLUE}📡 RPC URL:${NC} $RPC_URL"
echo -e "${BLUE}👤 Deployer:${NC} $DEPLOYER_ADDRESS"
echo -e "${BLUE}🎯 Target Address:${NC} $MULTICALL3_ADDRESS"
echo ""

# Step 1: Check if Multicall3 is already deployed
echo -e "${YELLOW}🔍 Step 1: Checking if Multicall3 is already deployed...${NC}"
CODE_SIZE=$(cast code $MULTICALL3_ADDRESS --rpc-url $RPC_URL 2>/dev/null | sed 's/0x//' | wc -c)

if [ "$CODE_SIZE" -gt 2 ]; then
    echo -e "${GREEN}✅ Multicall3 is already deployed at $MULTICALL3_ADDRESS${NC}"
    echo -e "${GREEN}   Code size: $CODE_SIZE bytes${NC}"
    exit 0
fi

echo -e "${YELLOW}⚠️  Multicall3 not found at canonical address${NC}"
echo ""

# Step 2: Check deployer balance
echo -e "${YELLOW}🔍 Step 2: Checking deployer balance...${NC}"
BALANCE=$(cast balance $DEPLOYER_ADDRESS --rpc-url $RPC_URL 2>/dev/null)

if [ -z "$BALANCE" ]; then
    echo -e "${RED}❌ Error: Could not fetch balance for $DEPLOYER_ADDRESS${NC}"
    echo -e "${YELLOW}   Please check your RPC URL: $RPC_URL${NC}"
    exit 1
fi

echo -e "${BLUE}   Current balance:${NC} $BALANCE wei"

if [ "$BALANCE" -lt "$REQUIRED_BALANCE" ]; then
    echo -e "${YELLOW}⚠️  Insufficient balance. Funding deployer...${NC}"

    # Step 3: Fund the deployer
    echo -e "${YELLOW}🔍 Step 3: Funding deployer address...${NC}"

    # Get funding account from environment or use default
    FUNDING_KEY="${DEPLOYER_PRIVATE_KEY}"

    if [ -z "$FUNDING_KEY" ]; then
        echo -e "${RED}❌ Error: DEPLOYER_PRIVATE_KEY not set in environment${NC}"
        echo -e "${YELLOW}   Please set DEPLOYER_PRIVATE_KEY in .env file or export it${NC}"
        exit 1
    fi

    # Send funds to deployer
    echo -e "${BLUE}   Sending $REQUIRED_BALANCE wei to $DEPLOYER_ADDRESS...${NC}"

    FUND_TX=$(cast send $DEPLOYER_ADDRESS \
        --value $REQUIRED_BALANCE \
        --private-key $FUNDING_KEY \
        --rpc-url $RPC_URL \
        --async 2>&1)

    if [ $? -ne 0 ]; then
        echo -e "${RED}❌ Error: Failed to fund deployer${NC}"
        echo -e "${RED}   $FUND_TX${NC}"
        exit 1
    fi

    echo -e "${GREEN}✅ Funding transaction sent${NC}"

    # Wait for transaction confirmation
    echo -e "${BLUE}   Waiting for confirmation...${NC}"
    sleep 5

    # Verify new balance
    NEW_BALANCE=$(cast balance $DEPLOYER_ADDRESS --rpc-url $RPC_URL)
    echo -e "${GREEN}✅ New balance: $NEW_BALANCE wei${NC}"
else
    echo -e "${GREEN}✅ Deployer has sufficient balance${NC}"
fi

echo ""

# Step 4: Deploy Multicall3 using pre-signed transaction
echo -e "${YELLOW}🚀 Step 4: Deploying Multicall3 contract...${NC}"
echo -e "${BLUE}   Publishing pre-signed transaction...${NC}"

DEPLOY_TX=$(cast publish $RAW_TX --rpc-url $RPC_URL 2>&1)

if [ $? -ne 0 ]; then
    echo -e "${RED}❌ Error: Failed to publish transaction${NC}"
    echo -e "${RED}   $DEPLOY_TX${NC}"
    exit 1
fi

echo -e "${GREEN}✅ Transaction published${NC}"
echo -e "${BLUE}   Waiting for confirmation...${NC}"
sleep 5

# Step 5: Verify deployment
echo -e "${YELLOW}🔍 Step 5: Verifying deployment...${NC}"
CODE_SIZE=$(cast code $MULTICALL3_ADDRESS --rpc-url $RPC_URL 2>/dev/null | sed 's/0x//' | wc -c)

if [ "$CODE_SIZE" -gt 2 ]; then
    echo -e "${GREEN}✅ SUCCESS! Multicall3 deployed successfully!${NC}"
    echo -e "${GREEN}   Address: $MULTICALL3_ADDRESS${NC}"
    echo -e "${GREEN}   Code size: $CODE_SIZE bytes${NC}"
    echo ""
    echo -e "${BLUE}╔════════════════════════════════════════════════════╗${NC}"
    echo -e "${BLUE}║  Multicall3 is now available for use on BEGA L2   ║${NC}"
    echo -e "${BLUE}╚════════════════════════════════════════════════════╝${NC}"
else
    echo -e "${RED}❌ Error: Deployment verification failed${NC}"
    echo -e "${RED}   Code size: $CODE_SIZE bytes${NC}"
    exit 1
fi
